import { useState } from 'react';
import axios from 'axios';

const LoginForm =()=>{
    const [username , setUsername ] =useState('');
    const [password , setPassword] =useState('');
    const [error, seterror] = useState('')

    const handleSubmit =async (e)=>{
        e.preventDefault();
        const authObject = { 'Project-Id':'f28adb50-6228-4ab7-9280-f19e2b22df4c', 'User-Name':username , 'User-Secret':password };
        try {
            await axios.get('https://api.chatengine.io/chats' , {headers:authObject});
            localStorage.setItem('username',username);
            localStorage.setItem('password',password);
            window.location.reload();
        }catch(error){
            seterror()
        }
    }
    return (
        <div className='wrapper'>

        <div className='form'>
        <h1 className='title'> chat App </h1>
        <form onSubmit={handleSubmit}>
            <input type='text' value={username} onChange={(e)=>{
                setUsername(e.target.value)
            }} className='input' placeholder='UserName...' required/>

            <input type='password' value={password} onChange={(e)=>{
                setPassword(e.target.value)
            }} className='input' placeholder='Password...' required/>
            <div align='center'><button type='submit' className='button'>
                <span>Start Chatting</span>
                </button></div>
        </form>
        </div>

        </div>
    )

}
export default LoginForm;
